css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("dailyjs.com") {
body,
#navigation li a.active,
#navigation li a:hover {
  background:#222 !important;
  color:#ccc !important;
}
#navigation,
span.tags a,
.highlight,
code, 
.aside,
#dsq-content .dsq-textarea .dsq-textarea-wrapper, 
#dsq-content .dsq-input-wrapper,
#dsq-content .dsq-textarea,
#dsq-content iframe {
  background:#111 !important;
  border-color:#333 !important;
}
#navigation li.icon a{
  background:#666 !important;
  border-color:#333 !important;
  padding-left:6px !important;
}
#navigation li.icon a:hover{
  background:#888 !important;
}
a:link, a:active, a:visited {
	color: #5FABF7;
}
.navbar .brand {
	color: #ccc;
	text-shadow: 1px 1px #555;
}
.navbar-inner {
    background-color: #444;
}
.subnav {
	background: #111;
  	color: #ccc !important;
  	text-shadow: 1px 1px #555;
  	border: 1px solid #111;
  	border-bottom-color: #1a1a1a;
  	border-left: none;
  	border-right: none;
  	box-shadow: none;
}
.nav-tabs {
  	border-bottom-color: #1a1a1a;
}
.nav-tabs > li > a, .nav-pills > li > a {
  	color: #ccc !important;
    border-left: 1px solid #666;
    border-right: 1px solid #555;
}
.nav > li > a {
	background: #333;
    border-left: 1px solid #222;
    border-right: 1px solid #222;
}
.nav-tabs > .active > a,
.nav-tabs > .active > a:hover,
.subnav .active > a,
.subnav .active > a:hover,
.nav > li > a.active,
.nav > li > a:hover {
	background: #222;
    border-left: 1px solid #555;
    border-right: 1px solid #444;
}
.nav-tabs > li > a:hover,
.nav-tabs > .active > a,
.nav-tabs > .active > a:hover {
    border: 1px solid #555;
}
.hero-unit,
.itemlist {
    background-color: #333;
  	text-shadow: 1px 1px #555;
}
.hero-unit h1 {
  color: #ccc !important;
  text-shadow: 1px 1px #444;
}
.itemlist h3 {
    background-color: #444;
}
.itemlist h3, .itemlist h4, .itemlist p {
  	color: #ccc !important;
  	text-shadow: 1px 1px #555;
}
.itemlist li {
	background-color: #333;
}
.itemlist ul.subscribe .social-button img {
	background-color: #eee;
    border-radius: 4px;
}
}
