css:
@namespace url(http://www.w3.org/1999/xhtml);

/*
Wikipédia Statique
Zéfling : http://ikilote.net
License: BSD
*/

@-moz-document domain("wikipedia.org") {

body {
  background-image:-moz-linear-gradient(top, white, white 40%, #f3f3f3) !important;
  background-image:linear-gradient(to bottom, white, white 40%, #f3f3f3) !important;
  background-size:100% 7em;
  background-repeat:no-repeat;
  background-attachment:fixed;
}

#mw-page-base,
#mw-head-base{
  z-index:2000;
  background-color:#F3F3F3;
}
#mw-head {
  background:-moz-linear-gradient(top, white, white 60%, #f6f6f6 98%, #a7d7f9);
  background:linear-gradient(to bottom, white, white 60%, #f6f6f6 98%, #a7d7f9);
  height:5em;
  left:10em;
  z-index:2000;
  overflow:hidden;
}

@media screen and (min-width: 982px) {
  #mw-head {
    left:11em;
  }
  #left-navigation {
    margin-left: 0;
  }
}

div#mw-head {
    width: 99% !important;
    top: 1px !important;
}

#left-navigation {
  left:0 !important;
}
#right-navigation{
  position:relative;
  right:10em;
}
#p-personal{
   right:10.75em !important;
}
#mw-page-base,
#mw-head-base,
#mw-head {
  position:fixed !important;
}
#content {
 margin-top:5em;
 z-index:0;
}
div.suggestions {
  z-index:2009 !important;
}

/* Ancres */

#bodyContent h2,
#bodyContent h3,
#bodyContent h4,
#bodyContent h5,
.anchor {
  overflow:hidden;
}
#bodyContent h2 >span[id],
#bodyContent h3 >span[id],
#bodyContent h4 >span[id],
#bodyContent h5 >span[id],
.anchor{
  position:relative;
}
#bodyContent h2 >span[id] {
  padding-top:4.3em;
}
#bodyContent h3 >span[id]{
  padding-top:5em;
}
#bodyContent h4 >span[id],
.anchor{
  padding-top:6em;
}
#bodyContent h5 >span[id] {
  padding-top:7em;
}

/*
.references li[id] {
  position:relative;
  padding-top:4.3em;
  overflow:hidden;
  display:inline;
clear:both;
}*/

/* Titre Flotant */


h1#firstHeading {
  border:0 none transparent !important;
  font-size:1.6em !important;
  margin-left:-14px !important;
  padding-top:0 !important;
  position:fixed !important;
  top:11px !important;
  z-index:100000 !important;
}
#content {
    padding-top:5.5em !important;
}

/* position des références */

ol.references li:target {
    background-color: transparent !important;
    padding-top: 8em;
    margin-top: -8em;
}

ol.references li:target .reference-text {
    background-color: rgb(221, 238, 255);
}


}
