css:
@-moz-document url-prefix("http://www.nicovideo.jp/cache/"){	
*{
	font-size:11px!important;
}
div#mainBox{
	top:0px!important;
	padding:0px!important;
	height:100%!important;
	overflow-x:hidden!important;
}
ul#folderList{
	height:94%!important;
	top:auto!important;
	bottom:0px!important;
	left:0px!important;
	padding:0px!important;
	z-index:1!important;
	background-color:#DEF!important;
	overflow-x:hidden!important;
}
div#root{
	height:6%!important;
	width:0px!important;
	padding:0px 0px 0px 150px!important;
	overflow:hidden!important;
	background-color:#FDD!important;
}
table.header{
	position:fixed!important;
	top:0px!important;
	left:150px!important;
	width:auto!important;
	display:none!important;
	-moz-border-radius:0px 0px 10px 0px!important;
	border:1px #202060 solid!important;
}
div#root:hover > table.header{
	display:block!important;
}
div#root > table.header{
	height:30px!important;
}
div#root > div{
	position:fixed!important;
	top:26px!important;
	left:0px!important;
}
a.sortheader{
	display:block!important;
}
/*
div#dialog{
	color:white!important;
	left:180px!important;
	background:none!important;
}
div#dialog h2{
	background:none!important;
}
*/
a{
	background:none!important;
	text-decoration:none!important;
}
a.video:hover{
	color:red!important;
}
a:active{
	position:relative!important;
	top:1px!important;
	left:1px!important;
}
}
