css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("leagueoflegends.com") {
.sca_not_agree .post_content, .sca_not_agree .message_footer, .sca_not_agree .avatar_top {opacity: 1 !important;}
.sca_not_agree { color: black; }
.sca_not_agree table { background-color: #e8dec1 !important; background-image: none !important; }
.sca_not_agree .avatar_top { background-image: none !important; }
.sca_not_agree td.left { background-color: #ded1a9 !important; background-image: none !important; }
/*.user_summoner_icon {display:none !important;}
.photo { display: none !important; }*/
.avatar_top {width:105px !important;}
.forum_post .left {width:105px !important;}
.forum_post .left .avatar {width:105px !important;}
.forum_post .left .avatar .photo { background: none !important; width:80px !important; height:90px !important;}
.left_orb {display:none !important;}
.right_orb {display:none !important;}
}
