@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("wikia.com") {
  .toolbar,.WikiaFooter,.WikiaHubBranding {
    visibility: hidden !important;
  }
  .CorporateFooter ul li {
    visibility: hidden !important;
  }
  #WikiaHeader {
    visibility: hidden !important;
    height: 0;
  }
}
