@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("forums.comodo.com") {

a[href*="friends.comodo.com"] { display:none !important } 

/* Hides The "Please Join Our Forums" Image */
img[src*="join_forum_bttn"] { display:none !important }

/* Hides The "Comodo Authentic & Secure" Image */
#cot_tl_fixed { display:none !important }

/* Hides The "Footer Ad" */
#footerarea { display:none !important }

}
