css:
/* original opera extension @ https://addons.opera.com/addons/extensions/details/cleantube/1.2.1/?display=en */
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url('http://www.youtube.com/'), url-prefix('http://www.youtube.com/watch') {
    /* kill stuff */
    #branded-playlist-module, #watch-sidebar{display:none !important}
    #comments-view{display:none !important}
    #watch-headline-user-info{display:none !important}
    #pagebottom{display:none !important}
    #watch-headline{display:none !important}
    
    /* kill vevo backgrounds */
    #content{background-color:transparent !important; background-image:none !important}
    #watch-headline *{color:inherit !important}
    #watch-branded-actions{display:none !important}
    
    /* enlarge the player */
    #watch-player, #watch-panel, #watch-actions{width:100% !important}
    #watch-player{height:480px !important}
    
    /* fix up */
    #pagetop{margin-bottom:20px !important}
    #pagebottom, #footer-container, #footer{height:0 !important}
}
