css:
@namespace url(http://www.mozilla.org/keymaster/gatekeeper/there.is.only.xul);

#nav-bar .toolbarbutton-1 > .toolbarbutton-menubutton-button,
#nav-bar .toolbarbutton-1 > .toolbarbutton-menubutton-dropmarker,
#nav-bar .toolbarbutton-1 {
    -moz-appearance: toolbarbutton !important;
    margin: 0 !important;
    box-shadow: none !important;
}

#navigator-toolbox[iconsize="large"][mode="icons"] > #nav-bar #back-button {
    border-radius: 4.5px 0 0 4.5px !important;
    margin: 1px 3px !important;
    padding: 1px 5px !important;
    -moz-margin-end: 0px !important;
    border: 1px solid !important;
    border-color: rgba(0,0,0,.12) rgba(0,0,0,.19) rgba(0,0,0,.38) !important;
}

#navigator-toolbox[iconsize="large"][mode="icons"] > #nav-bar #forward-button {
    -moz-margin-start: 0px !important;
    mask: none !important;
    padding: 1px 5px !important;
}
