css:
@namespace url("http://www.mozilla.org/keymaster/gatekeeper/there.is.only.xul");
@namespace xhtml url("http://www.w3.org/1999/xhtml");

@-moz-document url("about:addons"),
               url("chrome://mozapps/content/extensions/extensions.xul") {
  #categories {
    min-width: 0 !important;
  }

  .category > .category-name {
    display: none !important;
  }

  .global-warning-container {
    display: none !important;
  }

  .addon {
    padding: 0 !important;
    -moz-box-direction: reverse !important;
  }

  .addon[selected] {
    border-left-width: 2px !important;
  }

  .addon:not([selected]) {
    padding-left: 2px !important;
  }

  .addon > :-moz-any(.warning, .error, .pending) {
    margin-left: 36px !important;
  }

  .addon:not([selected]) > :-moz-any(.warning, .error, .pending) {
    display: none !important;
  }

  .addon :-moz-any(.icon-container, .icon) {
    width: 16px !important;
    height: 16px !important;
  }

  .addon:not([selected]) .content-inner-container {
    -moz-box-orient: horizontal !important;
  }

  .addon[selected] .basicinfo-container {
    margin-top: 3px !important;
  }

  .addon .name-container {
    font-size: inherit !important;
  }

  .status-control-wrapper {
    -moz-box-orient: horizontal !important;
    -moz-box-direction: reverse !important;
  }

  .update-info-container {
    -moz-box-direction: reverse !important;
  }

  .addon:not([selected]) .include-update {
    display: none !important;
  }
                 
  .addon:not([selected]) .addon-control {
    display: none !important;
  }
                 
  .addon:not([selected]) :-moz-any(.advancedinfo-container, .control-container) {
    display: none !important;
  }
                 
  .relnotes > xhtml|body {
    font-size: inherit !important;
  }
}
