css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("netvibes.com") {

/* save space on header */
#top{ margin-top: -29px !important;}
#top:hover{ margin-top: 0px !important;}
#topLinks{height:0 !important;}

/* flip between module content and config */
.editContent[style*="display: block;"] + .moduleContent {display: none !important;}

/* slimmer tabs */
#topLinks{padding-top: 3px !important;}
#divTabs{margin-top: 2px !important;}
#divTabs .icon{margin-top: 0 !important;}
#divTabs .actions-tab-close{margin: 0 0 4px 8px !important;}
#divTabs .actions-tab-options{margin-bottom: -5px !important;}
#divTabs li {padding: 3px 5px !important;}
#divTabs #aNewTab{
  padding: 4px 0 !important;
  text-transform: lowercase !important;
  border: none !important;
}
#footer{margin: 5px 0 !important;}
}
