css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("twitter.com") {

.meta.entry-meta { 
  text-transform: lowercase !important; 
}

#status { 
  height: auto !important; 
}

#friends { 
  margin-left: -3px !important; 
  overflow: hidden !important;
}

#friends span {
  float: left !important;
  margin-left: 3px !important;
}

#side div.section {
  margin-bottom: 10px !important;
}

#side div.section_header {
  margin-bottom: 3px !important;
}

br.clear {
  display: block !important;
  height: 0 !important;
  overflow: hidden !important;
}

#send_via_form br,
#side .promo  {
  display: none !important;
}

#send_via_form input,
#send_via_form label {
  float: left !important;
}

#send_via_form label {
  margin: 0 3px !important;
}

#loader { background-color: #fff !important; }
#loader img { display: block !important; }

.bottom_nav {
  overflow: hidden !important;
  padding: 4px 0pt !important;
}

#content #doingForm .bar {
  background-color: #fff !important;
}

body#show.status #footer {
  display: none !important;
}

#side ul.stats li:last-child span:last-child {
  font-family: "Georgia", "Serif";
  font-size: 1.2em;
}

}
