css:
@namespace url("http://www.mozilla.org/keymaster/gatekeeper/there.is.only.xul");

.tabbrowser-tab[label="(Untitled)"]:not([pinned]),
.tabbrowser-tab[label="New Tab"]:not([pinned]) .tab-icon-image {
          display: none !important;}

.tabbrowser-tab[label="(Untitled)"]:not([pinned]):not([blah]) label,
.tabbrowser-tab[label="New Tab"]:not([pinned]):not([blah]) label {
          display: none !important;}

.tabbrowser-tab[label="(Untitled)"]:not([pinned]),
.tabbrowser-tab[label="New Tab"]:not([pinned]) .tab-close-button {
      visibility: visible !important;}
