css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.basesmedicina.cl"), url-prefix("www.basesmedicina.cl") {

    #capitulo_central, #capitulo_espacio_iframe {
        height: auto!important;
    }

    #capitulo_espacio_iframe > iframe {
        height: 640px!important;
    } 

    #marco_contenedor {
        width: auto!important;
        padding: 0 40px;
    }

    #marco_contenedor table {
        width: 600px;
    }

    #marco_contenedor p,
    #marco_contenedor ul,
    #marco_contenedor ol {
        text-align: left!important;
    }

    #marco_contenedor .texto_simple_centrado {
        text-align: center!important;
    }


    #marco_contenedor p,
    #marco_contenedor ul,
    #marco_contenedor ol,
    #marco_contenedor td,
    .subtitulo_subrayado,
    .texto_negrita_centrado,
    .texto_simple_izqu,
    .texto_negrita_izq,
    #marco_contenedor td p,
    #marco_contenedor td ul {
        font-size: 18px!important;
        line-height: 1.5;
    }
     
    #marco_contenedor .titulo {
         font-size: 24px!important;
    }


    #marco_contenedor .subtitulo {
         font-size: 20px!important;
    }

}
