css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.pbs.org") { /* userstyles */

.copy p {color:black !important;}

}

@-moz-document domain("www.npr.org") {

.textsize,
.date { color : #AE2268 !important; }

#storytext p { color : black !important; }

ul.brand li a,
ol.line li a { color : #395A99 !important; }

h4 a {color: purple !important;}
h4 a:hover {color: blue !important;}
.commentbox p {color: black !important;}
.commentbox p a {color: #585800 !important;}

ul.edTag li,
.commentinfo .commentbody,
.commentinfo .commentbyline {color: black !important;}
.commentinfo .commentdate {color: #AE2268 !important;}
.bucketwrap ul li a, .bucket ul li a,
.storytools li a {color: #404000 !important;}

.transcript p {color: black !important;}
.transcript p.disclaimer {color: maroon !important;}

}
