css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("book.realworldhaskell.org") {

body {background:black !important; color:gray !important;}
a {color:white !important;}
.book, .preface, .chapter {background:#3B3131 !important;}
.zebra_a {background:#463E3F !important;}
.zebra_b {background:#2B1B17 !important;}

.screen, .note, .warning, .tip {background-color:#C9C299 !important;}
.screen, .note, .warning, .tip, .commenttoggle a {color:black !important;}
.commenttoggle  {color:black !important;}
}
