css:
@namespace url(http://www.mozilla.org/keymaster/gatekeeper/there.is.only.xul);

/* Shows Urlbar Icons on Hovered links */

.urlbar-input-layer-children {
    opacity: 1 !important;
}
.urlbar-over-link-path-label { 
    margin-right:90px !important; /*<---This value may be changed depending on number of icons in urlbar*/
}

/* Hides S4E Download button text "No Downloads" */

#status4evar-download-button[label="No Downloads"] > label {
    display: none !important; 
}
#status4evar-download-button[label="No Downloads"] .toolbarbutton-icon {
    margin: 0 !important;
}
