css:
@namespace url(http://www.mozilla.org/keymaster/gatekeeper/there.is.only.xul);

.tabbrowser-tabs .tabbrowser-tab[selected="true"]:not([pinned="true"]) {
   min-width: 250px !important;
   cursor: default !important;
}

.tabbrowser-tabs .tabbrowser-tab:not([selected="true"]):not([pinned="true"]):hover {
   min-width: 250px !important;
   -moz-transition-property: min-width !important;
   -moz-transition-delay: 1650ms !important;
   cursor: default !important;
}

#tabbrowser-tab-tooltip {
  display: none !important;
}


.tabbrowser-tabs[overflow="true"] .tabbrowser-tab:not([pinned="true"]):hover {
   min-width: 250px !important;
   -moz-transition-property: min-width !important;
   -moz-transition-delay: 850ms !important;
   cursor: default !important;
}
