css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("sports.yahoo.com"),
domain("huffingtonpost.com"),
domain("my.yahoo.com"),
domain("autos.yahoo.com"),
domain("yahoo.match.com"),
domain("finance.yahoo.com"),
domain("games.yahoo.com"),
domain("yahoo.net"),
domain("shine.yahoo.com"),
domain("movies.yahoo.com"),
domain("omg.yahoo.com"),
domain("realestate.yahoo.com"),
domain("shine.yahoo.com"),
domain("shopping.yahoo.com"),
domain("pulse.yahoo.com"),
domain("weather.yahoo.com"),
domain("www.yahoo.com"),
domain("us.yahoo.com"),
domain("fr.yahoo.com"),
domain("au.yahoo.com"),
domain("br.yahoo.com"),
domain("ca.yahoo.com"),
domain("cf.yahoo.com"),
domain("de.yahoo.com"),
domain("es.yahoo.com"),
domain("in.yahoo.com"),
domain("it..yahoo.com"),
domain("news.yahoo.com"),
domain("news.cn.yahoo.com"),
domain("qc.news.yahoo.com"),
domain("uk.news.yahoo.com"),
domain("us.news.yahoo.com"),
domain("fr.news.yahoo.com"),
domain("au.news.yahoo.com"),
domain("br.news.yahoo.com"),
domain("ca.news.yahoo.com"),
domain("cf.news.yahoo.com"),
domain("de.news.yahoo.com"),
domain("es.noticias.yahoo.com"),
domain("in.news.yahoo.com"),
domain("it.notizie.yahoo.com"),
domain("news.yahoo.com"),
domain("news.cn.yahoo.com"),
domain("qc.news.yahoo.com"),
domain("uk.news.yahoo.com") {
body,
#ynw-mrst,
.yn-tooltip,
.ynw-othstr,
.moreLnks,
.ynw-story,
.bul,
p,
.cnt,
.clr,
.first,
span,
cite,
#related-photo p,
#ynw-mTab-item1,
#ynw-mTab-item2,

/* Background */
table {background-color: rgba(10,10,10,.9) !important}


/* Divider Lines */
* {
border-color: #3e3e3e !important;

/* Articles Text */
color: #B2B5B7 !important;
text-shadow: none !important;
-webkit-box-shadow: none !important;
-moz-box-shadow: none !important;
}

/* Related/Video/Slidshow  DMG Purple*/
b,
b *,
strong,
strong *,
a:hover {
color: #5F4F74  !important;
}

h1,
h1 *,
h2,
h2 *,
h3,
h3 *,
h3 cite,
h4,
h4 *,
h5,
h5 *,
h6,
h6 *,
div.res h3 a.yschttl:visited,
pre,

/* My Y! Sender Names & Temp, Right-Side News Headlines (1) mid-grey purple */
a:visited {
color: #84698F !important;
}

/* Photo Captions, Journalist, Source, Date, "By," bright purple*/
span.url,
cite,
cite * {
color: #B38DC1 !important;
}

/* Hi, deep bright purple */
li,
i,
i *,
em,
div.res a[href*="http"] {
color: #704888 !important;
}



/* Search Bar Text, mid grey purple */
div { color: #84698F !important; background-color: #151515 !important; }

/* Search Bar Background & Outline, darkish grey purple*/
input[type="text"] {
	background-color: #5F4F74 !important;
	border: 2px solid #51238C !important;
	color: #D1D1D1 !important;

}
/* End of Search the news form */


/* Links, Headlines, & Subject Headings, drab cerulean */
a:link,
a:active
{ color: #30668F !important; }

/* Visited Links, deep dark purple */
a:visited
{ color: #492D5B !important; }



}
