@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("sourceforge.net") {

/* Hides Ads */
.ads { display:none !important }

/* Hides "comScore2" Ad, Bottom Left Corner */
.noscript-show { display: none !important; }

/* Hides Footer */
#footer_separator, #site-footer { display: none !important; }

}
