css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain(wat.tv)
{
/* Skip ads */
#Grid { display: block !important; }
#PreHome { display: none !important; }

#Header,
#Menu,
#Slideshow,
#Selection,
#Railway,
#Footer,
#bottomFooter,
#Toolbar,
#renderFlash .right,
.social_description
{
	display: none !important;
}
#contentPlayer
{
	width: auto !important
}
.content_player
{
	float: none !important;
	margin: auto !important;
}
#Premium
{
	position: absolute !important;
	height: 100% !important;
	width: 100% !important;
}
}
