css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("wrzuta.pl") {
 div.audio a.user,
 div.audio a.tag,
 div.audio div.quick_actions_wrap,
 div.audio div.aud_mini
 {display:none !important;}

div.audio
{
 width:100% !important;
 float:left !important;
 clear:both !important;
 margin:0 !important;
 padding:0 !important;
 border:0 !important;
}

div.audio a.title
{
 width:79% !important;
 float:left !important;
 clear:none !important;
 margin-left:1% !important;
 padding-bottom:7px !important;
 clear:none !important;
}

div.audio div.info
{
 float:left !important;
 width:20%!important;
 clear:none !important;
 border:0 !important;
}

div.audio div.info span.duration
{
 width:48% !important;
 float:left !important;
 clear:none !important;
 margin-left:1% !important;
 border:0 !important;
 padding-bottom:7px !important;
}

div.audio div.info span.size
{
 width:48% !important;
 float:left !important;
 clear:none !important;
 margin-left:1% !important;
 border:0 !important;
 padding-bottom:7px !important;
}


}
