css:
@namespace url(http://www.mozilla.org/keymaster/gatekeeper/there.is.only.xul);

#main-window[stylish-url^="http://www.facebook.com"] #urlbar{
	background: -moz-linear-gradient(top, #b8e1fc 0%, #a9d2f3 10%, #90bae4 25%, #90bcea 37%, #90bff0 50%, #6ba8e5 51%, #a2daf5 83%, #bdf3fd 100%) !important;
	color: #000 !important;
	border-radius: 5px !important;
	border: 1px solid rgba(0,0,0,0.5) !important;
	box-shadow: 0 0 5px #fff inset !important;
}

#main-window[stylish-url^="http://www.facebook.com"] #identity-box{
	box-shadow: 0 0 1px #000 !important;
	border: 1px solid #fff !important;
}
#main-window[stylish-url^="http://www.facebook.com"] #identity-box:hover{
	box-shadow: 0 0 1px #69f !important;
	border: 1px solid #fff !important;
}
