css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix("http://ameblo.jp/atsuko-maeda/entry"),
               url-prefix("http://ameblo.jp/atsuko-maeda/page"),
               url-prefix("http://ameblo.jp/atsuko-maeda/theme"),
               url-prefix("http://ameblo.jp/atsuko-maeda/day"),
               url-prefix("http://ameblo.jp/atsuko-maeda/archive"),
               url-prefix("http://ameblo.jp/atsuko-maeda/amemberentry"),
               url-prefix("http://ameblo.jp/atsuko-maeda/reader"),
               url("http://ameblo.jp/atsuko-maeda"),
               url("http://ameblo.jp/atsuko-maeda/") {
    #amebaBar,
    #message,
    #advertising3,
    #footer_ad_official,
    #sub_a,
    #themeBox,
    #exLinkBtn,
    .readerMainLink,
    .ad_frame,
    #sub_b .plugin,
    #calendar,
    #freespace,
    #ameblo {
        display: none !important;
    }

    #main {
        width: 100% !important;
        float: none !important;
        padding: 0 !important;
    }

    #sub_b {
        width: 100% !important;
        float: none !important;
    }

    #recent_entries .menu_title,
    #archives .menu_title {
        background: transparent !important;
        text-indent: 0 !important;
        height: auto !important;
        line-height: 1.5 !important;
        border-bottom: 1px solid #7C897E !important;
        padding: 0 0 0 5px !important;
        margin: 5px !important;
    }

    #recent_entries li,
    #archives li {
        list-style-type: disc !important;
        margin: 0 0 0 1em !important;
        padding: 0 !important;
        font-size: 12px !important;
    }

    .articlePaging,
    .topPaging {
        width: 100% !important;
    }

    .entry .contents,
    #message .contents {
        font-size: 14px !important;
    }
}
