css:
@-moz-document domain('atata.com') {
/* kill social buttom */ 
span.share { display:none !important; } 

/* return old nav */ 
#streams > nav { float:left !important; } 
#streams > nav .separator { right:0 !important; left:auto !important; margin:0 .8em 0 0 !important; } 
#streams > nav .updates { left:-24px !important; right:auto !important; } 
#streams > nav > ul > li { margin:0 1.6em .5em 0 !important; } 

#streams.closed_nav > nav { left:0 !important; right:auto !important; } 
#streams.closed_nav > nav .separator { left:0 !important; } 

#streams #page { float:right !important; } 
#streams.closed_nav #page { margin:0 0 0 1.2em !important; }
}
