css:
#urlbar  {font-size: 12px !important;}
#searchbar  {font-size: 11px !important;}


#toolbar-menubar, #main-menubar{
-moz-appearance: none !important;
background: #5282d3 !important;
}


#TabsToolbar[tabsontop=true]{
-moz-appearance: none !important;
background: -moz-linear-gradient(top, #5282d3, #3c66b5) !important;
border: none !important;
}


.tabs-alltabs-button[showTabCounter="true"],
#appmenu-toolbar-button {
font-size: 13px !important;
}
.tabs-alltabs-button[showTabCounter="true"]:not(:hover),
#appmenu-toolbar-button:not(:hover) {
color: #c8c8c8 !important;
}
