css:
@-moz-document domain('bluemassgroup.com') {

i, em, cite {
  font-style: italic;
}

#comments-list cite {
  font-style: italic;
  font-weight: normal;
}

#comments-list li {
  min-height: 0;
  padding-top: 0;
  border-top: 0;
}

#comments-list li li {
  margin-left: 0;
}

#comments-list .comment {
  padding-top: 1ex;
  margin-left: 4em;
  border-top: thin solid #CCC;
}

#comments-list .comment p {
  padding: 0;
  margin-left: 0px;
  font-size: 12px;
}

#comments-list .reply {
  margin-left: 0px;
}

#comments .reply a {
  font-size: 12px;
}

#comments img.avatar {
  position: absolute;
  float: none;
  left: -60px;
}

#comments .reply a.author-link {
  text-transform: capitalize !important;
}

.comment blockquote {
  margin-left: 2em;
  padding: 1ex 1em;
  background-color: #EEE;
  border: thin dashed #364C8B;
}

.comment *, #primary *, .recentcomment *, #sidebar p, .entry-content * {
  font-family: Verdana, sans-serif;
}

}
