css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("megafilm.kz") {

body{background:none!important;}
html>body>center>div#site>div#cont>table>tbody>tr:nth-child(1)
{display:none!important;}
html>body>center>div#site>div#cont>table>tbody>tr:nth-child(2)
{display:none!important;}

#cont .block:not(:first-child), #cont b .block{display:none!important;}

#site  {margin-top:20px!important;}

#cont table{width:1024px;}


#llogin, #rcol, #footer{display:none!important}
#dle-content div.blocknews div.block_text table tbody tr td div img
{
height:200px;
margin:5px 15px 0 -15px;
-moz-border-radius: .5em;
}

#dle-content .blocknews, #lcol .block {
display:block!important;
-moz-border-radius: 1em;
border:1px solid silver;
-moz-box-shadow: 1px 1px 6px rgba(20,20,20,51);
}

.blocknews .block_text td{
	font-family: sans-serif;
	font-size: 14px;
	line-height: 120%;
	word-spacing: normal;
	letter-spacing: normal;
	text-decoration: none;
	text-transform: none;
	text-align: left;
}
}
