css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("goal.com") {

#ad728x90,.ad300x250Module { display: none !important; }

html, #article_content {font-family: "Cronos Pro","Arial",sans-serif !important;}

#article_headline h2 , #article_content { font-size: 16pt !important; line-height: 1.5em !important;}

div#single_story_stage div#stage_storyContainer p, #stageModule_6_story_vert #mycarousel p { font-size: 12pt !important;}

#stageModule_6_story_vert * {line-height: 1em !important;}
div#newsFeedModule div.newsFeedList ul, div.stageRelatedLinks * {font-size: 12pt !important;}


#leftModules { width: 850px !important;}
#match-content-wrapper { width: 835px !important;}
#top_of_the_match, #scores,#score,#report_page_wrapper,#top_flop,#tab-nav,#score_team,#match_lineup{width: 816px !important;}
.team1_scorers, .team2_scorers {width: 396px !important;}
#score_team_left, #score_team_right {width: 341px !important;}
#top_match_team1,#top_match_team2{width: 405px !important;}
#team1_players_lineup, #substitutes_team1, #coach_team1{width: 394px !important;}
#team2_players_lineup, #substitutes_team2, #coach_team2{width: 398px !important;}


.player_info_tab_lineup{width: 394px !important;}


div#page-content { width: 1200px !important;}

#single_story_stage, #stageModule_6_story_vert, div#stage_storyContainer {height: auto !important;}

#bettingWidgetAnchor{display:none !important;}


#mostReadModule,#mostDiscussedModule, .comment-content p {font-size: 14pt !important;}
.comment-content {width: 680px !important;}
#comments-box {width: 800px !important;}

}
