css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("v2ex.com"), domain("v2ex.appspot.com") {

#Top {background-color: #136cb5 !important; background-image: none !important; box-shadow: 0 2px 5px rgba(255, 255, 255, 0.4); opacity: .7 !important;}

#topics_index > .cell {padding: 16px !important;}

#Search {display: none !important;}

#Wrapper {background: -moz-linear-gradient(top, #13abdc, #105e9c 17%) !important; background: -webkit-gradient(linear, 0 0, 0 17%, from(#13abdc), to(#105e9c)) !important;}

.content {color: #605959 !important;}

img[src*="avatar"] {display: inline-block !important; -webkit-border-radius: 5px !important; -moz-border-radius: 5px !important; border-radius: 5px !important; -webkit-box-shadow: 0 1px 3px rgba(0, 0, 0, .4) !important; -moz-box-shadow: 0 1px 3px rgba(0, 0, 0, .4) !important; box-shadow: 0 1px 3px rgba(0, 0, 0, .4) !important; -moz-background-size:50px 50px !important; -webkit-background-size:50px 50px !important; background-size:50px 50px !important; }

#Bottom {background-color: #13558D !important; border-top: 2px solid #0F5EA1 !important;}

#Bottom .fade, #Bottom a {color: #eee !important;}

#BottomMain:not(strong) div {display: none !important;}

#BottomMain small {color: #0D538F !important;}

}

@-moz-document url('http://www.v2ex.com/'), url('http://v2ex.appspot.com/') {
#topics_index > .cell:nth-child(1) {display: none !important;}
#Content > .box:nth-last-of-type(1) {display: none !important;}
}
