@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.webkinzinsider.com") {

/******************** Backgrounds ********************/
.shadow_2 {background: transparent !important;}

#left-col {background: #FF6699 !important;}

h3 {background: #CC00CC !important;}

.thead, .tcat, .tfoot, .vbmenu_control,.rounded-q1, .rounded-q2, .rounded-company {background: #63A4FF !important;}


/******************** Left Rounded Corners ********************/
h3 {-moz-border-radius-topleft: 10px; -moz-border-radius-bottomleft: 10px;}

/******************** Top Rounded Corners ******************/
.thead {-moz-border-radius-topleft: 10px; -moz-border-radius-topright: 10px;}

/******************** Bottom Rounded Corners ******************/
.tcat, .tfoot, .vbmenu_control {-moz-border-radius-bottomleft: 10px; -moz-border-radius-bottomright: 10px;}

/****************** Top-Left Rounded Corner ******************/
.rounded-company {-moz-border-radius-topleft: 10px;}

/****************** Top-Right Rounded Corner ******************/
.rounded-q2 {-moz-border-radius-topright: 10px;}

/******************** Hover ******************/

/******************** Hidden Objects ******************/
#middle, .shadow, .shadow_1 {background-image: none !important;}

#bodyContent > h3 {background-color: white !important;}
}
