css:
@-moz-document url(http://www.tumblr.com/), url-prefix(http://www.tumblr.com/), domain(www.tumblr.com){  


	 
div.l-header{
background: rgba(189,163,106,1) !important;
background-image: url('http://i52.tinypic.com/2woxggm.jpg') !important;
}

#left_column, #right_column {background-color: transparent !important;}

div.l-content.l-content.l-content {
     -moz-border-radius: 0px;
    -webkit-border-radius: 0px;
    -khtml-border-radius: 0px;
    border-radius: 0px;
}

#dashboard_index, #dashboard_edit_post, #dashboard_submissions, #dashboard_drafts,
#dashboard_post_queue, #dashboard_followers, #dashboard_members{
background-color:#FFFFFF;
background-image:url('http://i51.tinypic.com/307u3iw.jpg') !important;
background-repeat:repeat !important;
}

#dashboard_index div.l-content, #dashboard_submissions div.l-content, #dashboard_drafts div.l-content,
#dashboard_post_queue div.l-content, #dashboard_members #left_column, #right_column {background-color: transparent !important;}

div.l-content.l-content.l-content {
background: rgba(189,163,106,1) !important;
background-image: none !important;
}

#dashboard_edit_post{
background-color: #000000 !important
}

#right_column .dashboard_nav_item {
border-bottom:0px solid #eee !important;
background-image:none  !important
}

#right_column .dashboard_nav_item .dashboard_nav_title, #right_column .dashboard_nav_item .dashboard_nav_title a,
#right_column .dashboard_nav_item a,#right_column .dashboard_nav_item ul.dashboard_subpages,
#right_column .dashboard_nav_item ul.dashboard_subpages li a {
color:rgba(110,84,49,1) !important;
}

#right_column .dashboard_nav_item ul.dashboard_subpages li a.tracked_tag_control {
color:#888 !important ;
}

#right_column .dashboard_nav_item #dashboard_controls_radar_media_fold {
background: none !important;
}

#left_column #posts .notification .avatar {
border-color:transparent !important;
background-color:#000000 !important;
opacity:0.5 !important;
}

#left_column #posts .notification .avatar:hover{
opacity:100 !important;
}

.dashboard_switch_blog_menu_header{
background-color:#000000 !important;
color:#000 !important;
}

.dashboard_switch_blog_menu_item{
background-color:#FFFFFF !important;
color:#000 !important;
border:0px !important
}

.dashboard_switch_blog_menu_footer{
background-color:#ffffff !important;
color:#000 !important;
}

.dashboard_switch_blog_menu_wrapper{
background-color:#000000 !important;
}

#new_post_notice_container{
color:#fff!important
}

#new_post_notice_container a{
color:#fff !important
}

#dashboard_switch_blog_menu_current #dashboard_switch_blog_arrow_and_divider, #dashboard_switch_blog_arrow_current{
background-image:none !important
}

