css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("basilmarket.com") {

body {
	background-color: #FFE4F5 !important;
}

#night {
	background: url(http://i242.photobucket.com/albums/ff216/habokillen/Untitled-2.png) repeat-x !important;
}

div.innerWrap.innerWrapnight {
	background: url(http://i242.photobucket.com/albums/ff216/habokillen/header_sparklemarket-1.png) top center !important;
	height: 80px !important;
	background-repeat: no-repeat !important;
}

#day {
	background: url(http://i242.photobucket.com/albums/ff216/habokillen/Untitled-2.png) repeat-x !important;
}

div.innerWrap.innerWrapday {
	background: url(http://i242.photobucket.com/albums/ff216/habokillen/header_sparklemarket-1.png) !important;
	height: 80px !important;
	background-repeat: no-repeat !important;
}

div.innerWrapPP1 {
	background-image: none !important;
}

div.innerWrapPP2 {
	background-image: none !important;
}

ul.basilmenu a.mm-item-link-hover:active, ul.basilmenu a.mm-item-link-hover:link, ul.basilmenu a.mm-item-link-hover:visited {
	padding: 4px 7px;
	color: white;
	background: #a22c97 !important;
	border-left: 5px solid #a22c97 !important;
	border-right: 5px solid #a22c97 !important;
	text-decoration: underline;
}

ul.basilmenu div.mm-item-content {
	background: white;
	padding: 0px 0px 5px 0px;
	position: absolute;
	z-index: 9;
	border-top: 5px solid #a22c97 !important;
	border-bottom: 5px solid #a22c97 !important;
	border-left: 5px solid #a22c97 !important;
	border-right: 5px solid #a22c97 !important;
}

div.rowBar.rBC3.mt10 {
	background-color: #a22c97 !important;
}

div.mid.mt10 {
	background-color: #a22c97 !important;
}

div.mid {
	background-color: #a22c97 !important;
}

div.sawday {
	display: none !important;
}

div.sawnight {
	display: none !important;
}

a.pollvote {
	color: #000000 !important;
}

div.pollv {
	background-color: #a22c97 !important;
	border-color: #a22c97 !important;
}

div.pollvf {
	background-color: #a22c97 !important;
	border-color: #a22c97 !important;
}

div.rowBar.rBC3.mb10 {
	background-color: #a22c97 !important;
}

div.rowBar.rBC3.mb5 {
	background-color: #a22c97 !important;
}

div.left {
	background-color: #a22c97 !important;
}

div.topicBar {
	background-color: #a22c97 !important;
}

}
