css:
@-moz-document domain("omgubuntu.co.uk") {

/* Remove all the extras */
    .left-block, div[id="secondary"], div[id="post-ads"], div[id="primary"] {
        display: none !important;
    }

/* Adjust content */
    #content {
        margin: 22px 0 0 10px !important;
    }

    .post-inner {
        float: left !important;
        margin: 0 0 0 0 !important;
        width: 100% !important;
    }

    #main {
        background-color: #BFBFBF !important;
        margin: 13px 25px !important;
        padding: 0 0 25px !important;
        width: 96% !important;
    }
     
    #container {
        margin: 0 0 0 0 !important;
        width: 95% !important;
    }

    .entry-share {
        background: none repeat scroll 0 0 rgba(77, 31, 65, 0.898) !important;
        width: 530px !important;
    }

    #footer-new {
        color: #BFBFBF !important;
        background: #4D1F41 !important;
        left: 25px !important;
        margin-left: 0 !important;
        margin-top: 5px !important;
        width: 96% !important;
    }

    #header {
        height: 100px !important;
    }


    .dsq-textarea-background {
        background: #7F7F7F !important;
    }

    .tag-links li {
        background-color: #988292 !important;
    }

    body {
        background: #4D1F41 !important;
}

}
