@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("synonyme.woxikon.de") {

  /* neues Layout */

  div#pageIndex div#aContentMain,          /* Startseite, leere Suche */
  div#wallpaperTop,                        /* Banner oben */
  div#wallpaperRight,                      /* Banner rechts */

  *[id*=ADNET],                            /* Banner rechts */

  div#page div#header + div[style],        /* Google Anzeigen - Header */
  div#page div.gAdsHeader,                 /* Google Anzeigen */
  div#page div.gAdsFrame,                  /* Google Anzeigen */

  iframe[id*=google_ads],                  /* Werbung in der rechten Spalte */
  iframe[src*='admeld.com'],               /* Werbung in der rechten Spalte */
  ins[id*=aswift],                         /* Werbung in der rechten Spalte */
  ins[style*='width:300px'],               /* Werbung in der rechten Spalte */
  div#page div.sidebarAds,                 /* Werbung in der rechten Spalte */
  #cto300x250_divfc2aad42c8,               /* Werbung in der rechten Spalte */
  object[id^=cto],                         /* Werbung in der rechten Spalte */
  object[data*='criteo.net'],              /* Werbung in der rechten Spalte */
  div[id^=wr],

  div#page div#content iframe[src*=adnet], /* Werbung im Content-Bereich */
  div#page div#content object[id^=ad],     /* Werbung im Content-Bereich */
  div#page div#content object[id^=td],     /* Werbung im Content-Bereich */
  div#page div#content div.cc div[id^=ftdivid], /* Werbung im Content-Bereich */
  div#page div#content div.cc ins[style*='width:300px'], /* Werbung im Content-Bereich */
  div.cc.clearfix > div > div[onmouseout^=trkMoIf]       /* Werbung im Content-Bereich */

  {
    display: none !important;
    height: 0 !important;
  }

}
