css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("alandstidningen.ax"), domain("tidningen.ax") {

/* TAR BORT ANNONSER */
#banner-top, #banner-right,
.banner-item {
    display: none !important;
}

/* RENSAR UPP LAYOUTEN OCH CENTRERAR */

#wrapper {
    margin: 20px auto 0 !important;
    width: 830px !important;
}

#wrapper1, #header {
    border: 0 !important;
}

/* SKAPAR MELLANRUM MELLAN ELEMENTEN */

#wrapper1 {
    width: 830px !important;
}

#menu-top div:first-child {
    padding: 5px 0 !important;
}

#menu-top div:last-child {
    padding-top: 5px !important;
}

#menu-top div:last-child div:first-child {
    margin-left: 40px;
}

#main > .content {
    padding: 0 23px 10px !important;
}

#left { width: 137px !important; }
#main { width: 486px !important; }
#right { width: 197px !important; }

#wrapper2 {
    background-image: none !important;
}

/* FIXAR NAVIGERINGEN TILL VÄNSTER */

#left hr, #right hr {
    display: none !important;
}

#menu2 .root-item {
    margin-top: 15px !important;
}

#alandskortet {
    width: 125px !important;
}

}
