css:
@namespace url(http://www.w3.org/1999/xhtml);

body input:enabled:not([type='checkbox']):not([type='file']):not([type='submit']):not([type='reset']):not([type='button']):not([type='radio']):not([type='autocomplete']),
body textarea 
{
border: 1px #999 solid !important; 
-moz-border-top-colors: #727272 !important;
-moz-border-bottom-colors: #dadada !important;
-moz-border-left-colors: #b4b4b4 !important;
-moz-border-right-colors: #b4b4b4 !important;
}

body input:enabled:focus:not([type='checkbox']):not([type='file']):not([type='submit']):not([type='reset']):not([type='button']):not([type='radio']):not([type='autocomplete']), body textarea:focus
{
border: 1px #6fa1d9 solid !important ;
background-color: #ffffcc !important;
-moz-border-top-colors: #6fa1d9 !important;
-moz-border-bottom-colors: #6fa1d9 !important;
-moz-border-left-colors: #6fa1d9 !important;
-moz-border-right-colors: #6fa1d9 !important;
-moz-border-radius: 0 !important;
-moz-outline-radius: 2px !important;
-moz-outline-width: 2px !important;
-moz-outline-style: solid !important;
-moz-outline-color: #cce4ff !important;
}
