css:
@-moz-document domain(speedyshare.com) {

.blueline, .bottomlinks, .header, a.resultlink, 
 td>table, td[background], body>table:first-child>tbody:first-child>tr>
td:first-child+td, body>table:first-child>tbody:first-child>tr>
td:first-child+td+td, body>table:first-child+*+*>tbody>tr>td:first-child,
body>table:first-child+*+*>tbody>tr>td:first-child+td+td,
body td>p>a {display: none !important; }

body>table:first-child {margin: auto !important; width:235px !important; }

.resultlink {font-size: 35px !important;}

.respic{float: none !important; vertical-align: middle !important; }

body>table td>*:first-child+*+p {text-align: center; }

body>*:first-child+*+* {margin-top: 90px; }

font.result {font-size: 18px !important; height: 500px !important; }

body>table:first-child {height: 120px !important; }

}
