@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("mydigitallife.info") {

/* Hides "Social" Buttons */
#Social_Container { display:none !important }

/* Hides Comments */
.h2comments, .commentlist, #respond, #colLeft > P { display:none !important }

/* Hides Footer */
#footer { display:none !important }

/* Hides Right Side-bar */
#colRight { display:none !important }

/* Widen Story Div To 100% Width */
#content, #colLeft { width: 100% !important; }

/* Hides Un-needed Background Image */
#content.twocols { background: none !important; }

}
