css:
@-moz-document domain(dshini.net) {

.img_user_image_overlay, .img_wish_image_overlay {display: none!important}
#ds_sidebar div.user_image, #ds_sidebar div.wish_image {
    background: #F2F2F2!important;
    border: 1px solid #E0E0E0!important;
    border-bottom: none!important;
    border-radius: 15px 15px 0 0;
    height: 185px!important;
    width: 185px}
#ds_sidebar p.user_name {
    background: #F2F2F2!important;
    border: 1px solid #E0E0E0!important;
    border-top: none!important;
    border-radius: 0 0 15px 15px;
    margin-bottom: 6px!important;
    padding-top: 4px!important;
    width: 185px!important;
    z-index: 10000!important}
#ds_sidebar div.user_image img, #ds_sidebar div.wish_image img {
    border-radius: 10px;
    box-shadow: 0 0 8px rgba(0,0,0,0.7);
    padding: 0!important;
    margin: 10px 0 0 10px!important}
#ds_sidebar div.wish_image {
    border: 1px solid #E0E0E0!important;
    border-radius: 15px;
    margin-bottom: 14px!important;
    padding-bottom: 6px!important}
}
