css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("forum.runescape.com") {

body {
	background: #333 url("data:image/png;base64,") top left repeat-x !important;
	color: #fff !important;
	font-family: Tahoma, Arial, Helvetica, sans-serif !important;

}

a { color: #999999 !important; }

table,tr,td {
border-color: #fff !important;
color: #fff !important;
}

tr {
background: #000 !important;
}

td[class="groupname"],[class="groupend"] {
background: #333 !important;
}

div[id="content"],[class="bk"],[class="cont"] { border-color: #fff !important;
border-width: 1px !important;}

div[class="msgcreator uname"],td[class="leftpanel"] {
background: #444 !important;
}

}
