css:
@-moz-document domain("badgerandblade.com") {

body {
  min-width: 0px !important;
}

.contentBoxTopRight {
  background: #95a9fe !important;
}

.body_wrapper {
  background: #e0e0fe !important;
}

.bbcode_quote {
  background: #d0d0d0 !important;
}

div.userinfo {
  background: #d0d0d0 !important;
}

div.userinfo_noavatar {
  background: #d0d0d0 !important;
}

div.username_container {
  text-align: left !important;
}

.usertitle, .rank {
  text-align: left !important;
}

.bbcode_container div.bbcode_quote div.message {
  padding: 0.25em 0 0.25em 0 !important;
}

h2.posttitle {
  padding: 0 0 0 0 !important;
}

h2.posttitle > img {
  display: none !important;
}

.postbit .postrow {
  padding-bottom: 1.0em !important;
}

.signature {
  padding-top: 0.25em !important;
  margin-top: 0.25em !important;
}

.signaturecontainer {
  color: #999999 !important;
  font-size: small !important;
  font-family: inherit  !important;
  padding-top: 0px !important;
  margin-top: 0px !important;
  text-align: inherit  !important;
}

.signaturecontainer br {
  font: 0px  !important;
  padding: 0 0 0 0 !important;
  margin: 0 0 0 0 !important;
}

div.signaturecontainer br ~ br {
  display: none;
}

.signaturecontainer * {
  color: #999999 !important;
  font-size: small !important;
  font-family: inherit  !important;
  padding-top: 0px !important;
  margin-top: 0px !important;
  text-align: inherit  !important;
}

}
