css:
@namespace url(http://www.w3.org/1999/xhtml);


@-moz-document domain("nk.pl")
{

#page {
    width: 100% !important;
}

#last_visitors {
   display: none !important;
}

#page_header {
   display: none !important;
}

#applications_page .applications_page .left {
    display: none !important;
}

#applications_page .applications_page h1 {
    display: none !important;
}

#applications_page .applications_page .right, #applications_page .applications_page .center {
    width: 100% !important;
}

#applications_page .applications_page .applications_breadcrumbs {
    display: none !important;
}

#page_footer {
    display: none !important;
}

.ad {
    display: none !important;
}

#nktalk_bar {
    display: none !important;
}



html.menu_old > body > div#nktalk_wrapper > div#nktalk_container > iframe#nk_portal > html.menu_old > body > div#applications_page > div#page > div#page_content.applications_page > div#content_main.content_main_wide > div.right > div.box > div.box_content > div#application_frame > div#application_gadget > div.gadgets-gadget-content > iframe#remote_iframe_0.gadgets-gadget > html > body > div#main > p
{
    display: none !important;
}

html > body > div#main > p > iframe{
    display: none !important;
}



}
