css:
@-moz-document domain(pulsemusic.proboards.com) {
body          { font-family: Arial !important; font-size:12px !important; margin: 0px !important; padding: 0px !important; }
td            { font-family: Arial !important; color: #000000 !important; font-size:11px !important; }
input, textarea         { font-family: Arial !important; font-size: 9pt !important; color: #000000 !important; }
select, optgroup       { font-family: Arial !important; font-size: 7pt !important; color: #000000 !important; }
a:link        { text-decoration: none !important; color: #3A4E57 !important; }
a:visited     { text-decoration: none !important; color: #3A4E57 !important; }
a:hover       { text-decoration: underline overline !important; color: #3A4E57 !important; }
.nav          { font-size: 10px !important; text-decoration: none !important; color: #000000 !important; }
.nav:link, .nav:visited     { font-size: 10px !important; text-decoration: none !important; color: #3A4E57 !important; }
.nav:hover    { font-size: 10px !important; text-decoration: none !important; color: #3A4E57 !important;  text-decoration: underline !important; }
.windowbg, .windowbg2     { background-color: #EFEFEF !important; font-size: 11px !important; color: #000000 !important; }
.titlebg      { background-color: #143443 !important; font-style: normal !important; font-weight: bold !important; font-size: 12px !important; }
.popuptitlebg { background-color: #4080B0 !important; font-style: normal !important; font-weight: bold !important; font-size: 12px !important; color: #F1F1F1 !important; }
.catbg, .newstitlebg        { background-color: #9AB3C7 !important; color: #FFFFFF; font-weight: bold !important; font-size: 13px !important; }
.titletext, .titletext a, .cattext, .newstitlebg b, .titlebg, .titlebg b,  .titlebg span, .titlebg a, .catbg b { color: #ffffff !important; }
#fscroller { color: #000 !important; font-style: normal !important; font-size: 12px !important; }
.hr           { color: #000000 !important; background-color: #000000 !important;}
.bordercolor  { background-color: #000000 !important; }
.quote        { font-size: 10px !important; color: #000000 !important; background-color: #EFEFEF !important; }
.code         { font-size: 10px !important; font-family: Courier New !important; color: #000000 !important; background-color: #EFEFEF !important; }
}
