css:
@-moz-document domain('reddit.com') {
    .state-button a, .report-button a, .share-button a, .sidebox.create, .sidecontentbox, .side .submit div.spacer, li.share { display: none !important; }

    .thing .title a.title:visited {
      color: rgb(190,200,255) !important;
    }

    .entry .title { font-size: 1.32em !important; }

    a.comments { font-size: 1.1em !important; }

    .rank { visibility: hidden !important; }

    div.link {
      -webkit-border-radius: 0px 15px 15px 0px !important;
      -moz-border-radius: 0px 15px 15px 0px !important;
      border: 1px solid rgba(255,255,255,0) !important;
      position: relative!important; 
      overflow: auto !important; 
      padding: 8px !important;
    }

    .content #siteTable.sitetable.linklisting .link:hover { 
      background-color: rgba(200,215,255,0.1) !important;
      border: 1px solid rgba(0,0,0,0.1) !important;
     }

    .content #siteTable.sitetable.linklisting .link:hover ul.flat-list.buttons li .hide-button span a {
      display: block !important;
      text-indent: -100px !important;
      height: 100% !important;
      width: 26px !important;
      position: absolute !important;
      left: 0% !important;
      top: 0% !important;
      overflow: hidden !important;
      background: rgba(51,102,153,0.1) url(data:image/png;base64,) no-repeat center center !important;
    }

    .content #siteTable.sitetable.linklisting .link:hover ul.flat-list.buttons li .hide-button span a:hover 
    {
      background: rgba(255,25,40,0.3) url(data:image/png;base64,) no-repeat center center!important;
    }

    .content #siteTable.sitetable.linklisting .link:hover ul.flat-list.buttons li .hide-button span a:active {
      background:rgba(255,0,0,0.75) url(data:image/png;base64,) no-repeat center center!important;
    }

    div.side, body > a[name="content"] {
     width: 220px !important;
    }

    #header-bottom-right {
     width: 200px !important;
    }

    div#header div#header-bottom-left a#header-img-a {
      -webkit-box-shadow: 0px 0px 0px rgba(0,0,0,0) !important;
      -moz-box-shadow: 0px 0px 0px rgba(0,0,0,0) !important;
    }

    #search input[type="text"] {
      width: 190px !important;
    }

    #ad-frame {
      width: 190px !important;
      height: 190px !important;
    }
}

@-moz-document url-prefix('http://www.redditmedia.com/ads') {
    #ad a img {
     width: 185px !important;
     height: auto !important;
     max-height: 190px !important;
    }

    #ad embed {
     width: 180px !important;
     height: auto !important;
    }
}
