css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("tuaw.com") {
#hat, .leader, #slidebox, #footer, #super-footer > *, #meta-bar, .fb_iframe_widget, .aol-share, .body > br, #last, #sub-content, #m-carousel, #breaking { display: none !important; }
body { margin-top: 16px !important; }
#aol-comments { padding: 0px 0px 1px 0px !important; }
#post .body { float: none !important; width: auto !important }
#post .header { width: 100% !important; } 
#main-content { width: auto !important; float: none !important; }
#latest-news { margin-top: 0px !important; }
#latest-news > div { width: 470px !important; margin-right: 20px !important; }
#latest-news > div:last-child { margin-right: 0px !important; }
#latest-news li a { width: auto !important; }
#filter-bar, #river { width: 100% !important; }
#river .header, #river .body { width: 808px !important; }
#super-footer { border-top: 8px solid #FFF !important; }
#micro-posts .m-post .body { width: 707px !important; }
}
