@-moz-document domain("dashboard.bloglines.com") {

* { font-family: ubuntu !important; }

.entry-header .entry-innerTitle { font-size: 20px !important; font-weight: bold !important; color: #000 !important; }

.expandedView .focus .entry-content, #groupItems-recentlyRead .read .entry-content { color: #222 !important; font-size: 14px !important; }

#mosaicView-separator .read, #smartreader-feeds-main .focus .entry-body { border: 2px solid #1D92C4 !important; }

#mosaicView-separator .read, #smartreader-feeds-main .entry-body { border: 2px solid #ccc !important; }

}
