css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("forums.somethingawful.com"),domain("archives.somethingawful.com"),domain("forum.somethingawful.com") {

body {
    background-color: #000 !important;
    color: #ddd !important;
}


/* _________________________________________________  headers
   _________________________________________________ */

td.title, td.icon, td.replies, td.rating, td.posts, td.banned, #pm td, #filter, .altcolor1 td, #main_full td, div.standard {
    background-color: #323232 !important;
    border: 1px solid #434343 !important;
    color: #ddd !important;
}

td.author,  td.icon2, td.views, td.lastpost, td.moderators, td.users, td.archived, td.topics, .altcolor2 td, table.standard td  {
    background-color: #393939 !important;
    border: 1px solid #434343 !important;
    color: #ddd !important;
}

th.icon, th.icon2, th.title, th.replies, th.rating, th.author, th.rating, th.lastpost, th.views, .category, #pm th, #info th, th.moderators, .forumbar, .threadbar, #main_full th, .standard h2, table.standard th {
    background-color: #434343 !important;
    border: 1px solid #434343 !important;
    color: #ddd !important;
}

/* _________________________________________________  text
   _________________________________________________ */

*               { font-family: Calibri !important; }
a               { color: #4F94CD !important; }
a:hover         { text-decoration: none !important; }
dt.author, h4   { color: #698B69 !important; }
.author.op      { color: #4A708B !important; }
.qb2 h4         { color: #000 !important; }
.moderators     { text-align: right !important; }

.qb2, textarea { 
    background-color: #4A708B !important;
    border: 1px solid #000 !important;
    padding: 10px !important;
    color: #fff !important;
}

/* _________________________________________________  [code]
   _________________________________________________ */
pre { 
    background-color: #698B69 !important;
    border: 1px solid #000 !important;
    padding: 10px !important;
    color: #fff !important;
    font-family: Consolas !important;
}

pre hr { 
    color: #333 !important;
    background-color: #333 !important;
    height: 1px !important;
    border: 0 !important;
}

img.timg {visibility:visible !important; border: none !important; } /* fix for images not showing up in quotes */

/* _________________________________________________  forum drop-down
   _________________________________________________ */
select {
    background-color: #323232 !important;
    border: 1px solid #434343 !important;
    color: #ddd !important;
}
option {
    background-color: #4A708B !important;
    border: 1px solid #000 !important;
}


#mp_bar {
    background-color: #393939 !important;
    border: 1px solid #434343 !important;
    color: #ddd !important;
    padding: 5px !important;
}

#globalmenu {
    background-color: #333 !important;
}
#container {
    width: 90% !important;
    margin-left: auto !important;
    margin-right: auto !important;
}

/* ad removal */
#ad_banner_user, .ad_banner_oma, #searchboxes, #nav_purchase, #copyright, #notregistered, #unregskyscraper { display: none !important; }

span.spoiler { color: #000000; background-color: #000; } /* fix for spoiled text */


/* _________________________________________________  search
   _________________________________________________ */

div.search { padding-bottom: 10px !important }

div.search_right {
    background-color: #393939 !important;
    border: 1px solid #434343 !important;
    color: #ddd !important;
}

div.search_right option { 
    background-color: #4A708B !important;
    border: 1px solid #000 !important;
}

}
