css:
/* AGENT_SHEET */
@-moz-document url-prefix(http://)
{	
	body *{
		background: hsl(210,30%,50%) !important;
		color: hsl(210,70%,20%) !important;
		border: hsl(210,30%,30%) !important;
	}

  #label,label, label *{
		background: hsl(210,30%,50%) !important;
		color: hsl(210,70%,20%) !important;
		border: hsl(210,30%,30%) !important;
	}
	
	h1,h,h2,h3,h4,h5,b,u,i{
		color: hsl(210,100%,30%) !important;
	}
	
	a:visited{
		background: hsl(210,60%,70%) !important;
		color: hsl(210,70%,0%) !important;
	}
	
	a:hover{
		background: hsl(210,60%,10%) !important;
		color: hsl(210,100%,50%) !important;
	}
	
	a:link, a:active{
		/* background: hsl(210,60%,70%) !important; */
		color: hsl(210,100%,30%) !important;
	}

	input *,input,text,#input,#text,edit,#edit,message,#message,select,#select{
		background: hsl(210, 30%, 50%) !important;
		color: hsl(210, 70%, 20%) !important;
		border: hsl(210,30%,30%) !important;
	}
}
