css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix(http://www.google.se), url-prefix(http://www.google.no) {
  img[src$="/images/logo.gif"] {
    width: 0 !important;
    height: 0px !important;
    padding-left: 240px !important;
    background-image: url(http://i1182.photobucket.com/albums/x446/Erwin_Adonay/Halo_Reach_Noble_Team_by_ezra96.jpg) !important;
    background-position: center left !important;
    background-repeat: no-repeat !important;
  }

  div[title="Google"] {
    background-image: url(http://i1182.photobucket.com/albums/x446/Erwin_Adonay/Halo_Reach_Noble_Team_by_ezra96.jpg) !important;
    background-position: center left !important;
    background-repeat: no-repeat !important;
    width: 1023px !important;
    height: 240px !important;
  }

  div[title="Google"] > div {
    left: 210px !important;
    top: 95px !important;
    opacity: 0 !important;
  }
}




@-moz-document url-prefix("http://www.google.com") {

  img[src$="/images/logos/ps_logo2.png"]{
  width: 0px !important;
  background-image: url(http://i1182.photobucket.com/albums/x446/Erwin_Adonay/Halo_Reach_Noble_Team_by_ezra96.jpg)!important;
  background-position: center left !important;
  background-size: 800px ;
  padding-left: 1000px !important;
  padding-top: 60px !important;
  background-repeat: no-repeat;

  }
}

@-moz-document url-prefix(http://www.google.fr),url-prefix("http://www.google.de"),url-prefix("http://www.google.es"),url-prefix("http://www.google.it"), url-prefix(http://www.google.co.uk/) {
  img[src$="/images/logo.gif"] {
    width: 0px !important;
    height: 50000px !important;
    padding-left: 0px !important;
    background-image: url(http://i1182.photobucket.com/albums/x446/Erwin_Adonay/Halo_Reach_Noble_Team_by_ezra96.jpg) !important;
    background-position: center left !important;
    background-repeat: no-repeat !important;
    top: 0px !important;
  }

  div[title="Google"] {
    background-image: url(http://i1182.photobucket.com/albums/x446/Erwin_Adonay/Halo_Reach_Noble_Team_by_ezra96.jpg) !important;
    background-position: bottom left !important;
    background-repeat: no-repeat !important;
    background-size: 800px !important;
    padding-right: 500px !important;
    width: 1023px !important;
    height: 250px !important;
  }

  div[title="Google"] > div {
    left: 750px !important;
    top: 140px !important;
    opacity: 1 !important;
  }
}
