css:
@-moz-document domain("vkontakte.ru") {
body {
text-align: left !important; margin: 0px !important; padding: 0px !important;
}

#bFooter,.leftAd,div#quickSearch,#myLink,.add {
display:none !important;
}

div#sideBar,div#pageHeader,div#wrapH,div#wrapHI,div#header {
position: fixed !important;
z-index: 99 !important;
}

div#wrapHI #header { 
overflow: hidden !important;
height: 12px !important;
line-height: 14px !important; 
}

div#sideBar,div#pageBody,div.pageBody {
margin-top: 45px !important;
}

div#wrapH,div#wrapHI,div#header {
width: 606px !important;
}

div#content {
padding-top: 24px !important;
}

#wall .bOpen .flexHeader,#wall .bShut .flexHeader {
background-repeat: no-repeat !important;
background-image: url('http://plusvkontakte.narod.ru/img/wall_post.gif') !important;
background-position: 2px 2px !important; 
}

#prevp,#nextp {
padding-left: 30px !important;
color: white !important;
background-repeat: no-repeat !important;
background-position: center !important;
background-color: white !important; 
text-decoration: none !important;
}
#prevp { 
background-image: url('http://plusvkontakte.narod.ru/img/control_start_blue.png') !important;
}

#nextp {
background-image: url('http://plusvkontakte.narod.ru/img/control_end_blue.png') !important;
}

}
